import React from "react";
import axios from "axios";


export default class WriteCountry extends React.Component {
  state = {
    name: "",
  };

  handleChangeName = e => {
    console.log(this.state.name);
  }
  handleChangeContinent= e => {
    console.log(this.state.continent);
  }
  handleChangeCountry = e => {
    console.log(this.state.country);
  }
  handleChangeInfo= e => {
    console.log(this.state.info);
  }
  handleChangeImageUrl = e => {
    console.log(this.state.imageurl);
  }
  handleSubmit = async event => {
    event.preventDefault();

    axios
      .post("http://localhost:2500/importantCities", {
        name: this.state.name,
      })
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  };
  render() {
    return (
      <form>
        <label htmlFor="name-input">Enter a City:</label>
        <textarea id="name-input" onChange={this.handleChangeName}></textarea>
        <label htmlFor="continent-input">Enter a City:</label>
        <textarea id="continent-input" onChange={this.handleChangeContinent}></textarea>
        <label htmlFor="country-input">Enter a City:</label>
        <textarea id="country-input" onChange={this.handleChangeCountry}></textarea>
        <label htmlFor="info-input">Enter a City:</label>
        <textarea id="info-input" onChange={this.handleChangeInfo}></textarea>


        <button onClick={this.handleSubmit}>Click</button>
      </form>
    );
  }
}
