import React from "react";
import axios from "axios";
import Axios from "./Axios";
import Europe from "./Europe";
import Asia from "./Asia";
import Australia from "./Australia";
import NorthAmerica from "./NorthAmerica";
import SouthAmerica from "./SouthAmerica";
import Africa from "./Africa";
import Antartica from "./Antartica";

export default class WriteCountry extends React.Component {
  state = {
    name: '',
    continent: '',
    country: '',
    info: '',
    imageUrl: '',
  }



  handleChangeName = e => {
      console.log(this.state.name);


    this.setState({ name: e.target.value });
  }
  handleChangeContinent = e => {
    console.log(this.state.contienent);


  this.setState({ continent: e.target.value });
}
handleChangeCountry = e => {
  console.log(this.state.country);


this.setState({ country: e.target.value });
}
handleChangeInfo = e => {
  console.log(this.state.info);


this.setState({ info: e.target.value });
}
handleChangeImageUrl= e => {
  console.log(this.state.imageUrl);


this.setState({ imageUrl: e.target.value });
}


  handleSubmit = async event => {
    event.preventDefault();

      axios.post('http://localhost:2500/importantCities', {
        name: this.state.name,
        county: this.state.category,
        continent: this.state.continent,
        info: this.state.info,
        imageUrl: this.state.imageUrl,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  }
  render() {
    return (
    <form>
        <label htmlFor="name-input">Enter a City:</label>
        <Input id="name-input" onChange={this.handleChangeCity}></Input>
        <label htmlFor="country-input">Enter the country of the City:</label>
        <Input id="country-input" onChange={this.handleChangeCity}></Input>
        <label htmlFor="continent-input">Enter a continent of the City:</label>
        <Input id="continent-input" onChange={this.handleChangeCity}></Input>
        <label htmlFor="info-input">Enter info of the City:</label>
        <Input id="info-input" onChange={this.handleChangeCity}></Input>
        <label htmlFor="picture-input">Enter a link for a imagen of the City:</label>
        <Input id="picture-input" onChange={this.handleChangeCity}></Input>
        <Button onClick={this.handleSubmit}>Click</Button>
      </form>
    )
  }
}
