import React from "react";
import Axios from "./Axios";
import Europe from "./Europe";
import Asia from "./Asia";

export default class Form extends React.Component {
  state = {
    isDisplayed: false,
    continent: "none"
  };
  changeIsDisplayed = () => {
    this.setState({
      isDisplayed: true
    });
  };
  render() {
    if (this.state.isDisplayed) {
      if (this.state.continent === "Europe") {
        return <Europe />;
      }
      if (this.state.continent === "Europe") {
        return <Asia />;
      } else {
        return <Axios />;
      }
    }
    return (
      <form className="form-continents">
        <div className="form-row">
          <label htmlFor="contient">
            Select a continent taht you like to visit
          </label>
          <select
            id="continent"
            name="continent"
            value={this.state.continent}
            onChange={e =>
              this.setState({
                continent: e.target.value
              })
            }
          >
            <option value="none">None</option>
            <option value="Europe">Europe</option>
            <option value="North America">North America</option>
            <option value="South America">South America</option>
            <option value="Africa">Africa</option>
            <option value="Asia">Asia</option>
            <option value="Australia">Australia</option>
            <option value="Antartica">Antartica</option>
          </select>
          {console.log(this.state.continent)}
          <br></br>
          <button onClick={this.changeIsDisplayed}>Click</button>
        </div>
      </form>
    );
  }
}
